// Données des communes IDF par département
// Source: INSEE Code Officiel Géographique 2024

interface CityData {
  slug: string;
  name: string;
  postalCodes: string[];
  department: string;
  departmentName: string;
  population?: number;
}

function slugify(text: string): string {
  return text
    .toLowerCase()
    .normalize('NFD')
    .replace(/[\u0300-\u036f]/g, '')
    .replace(/[^a-z0-9]+/g, '-')
    .replace(/(^-|-$)/g, '');
}

