// Format for TypeScript output
function formatCityForTS(city: CityData): string {
  const population = city.population ? `, population: ${city.population}` : '';
  return `  { slug: "${city.slug}", name: "${city.name}", postalCodes: ${JSON.stringify(city.postalCodes)}, department: "${city.department}", departmentName: "${city.departmentName}"${population} },`;
}

